import React, { useState } from 'react';

const BudgetTable: React.FC = () => {
    const [income, setIncome] = useState<number>(0);
    const [expenses, setExpenses] = useState<{ [key: string]: number }>({});
    const [subTotal, setSubTotal] = useState<number>(0);

    const handleIncomeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = parseFloat(e.target.value);
        setIncome(value);
        updateSubTotal(value, expenses);
    };

    const handleExpenseChange = (category: string, value: number) => {
        setExpenses(prevExpenses => {
            const updatedExpenses = { ...prevExpenses, [category]: value };
            updateSubTotal(income, updatedExpenses);
            return updatedExpenses;
        });
    };

    const updateSubTotal = (income: number, expenses: { [key: string]: number }) => {
        const totalExpenses = Object.values(expenses).reduce((acc, curr) => acc + curr, 0);
        setSubTotal(income - totalExpenses);
    };

    return (
        <div>
            <h2>Budget Table</h2>
            <div>
                <label>
                    Income:
                    <input type="number" value={income} onChange={handleIncomeChange} />
                </label>
            </div>
            <div>
                <h3>Expenses</h3>
                {Object.keys(expenses).map(category => (
                    <div key={category}>
                        <label>
                            {category}:
                            <input
                                type="number"
                                value={expenses[category]}
                                onChange={(e) => handleExpenseChange(category, parseFloat(e.target.value))}
                            />
                        </label>
                    </div>
                ))}
            </div>
            <div>
                <h3>Subtotal: {subTotal}</h3>
            </div>
        </div>
    );
};

export default BudgetTable;
